import axios from "axios";

export const GET_LIST_JOB = 'GET_LIST_JOB'

export const getListJob = () =>{
    // dispateh penghubung
    console.log('2.action')
    return (dispatch) =>{
        // loading
        dispatch({
            type : GET_LIST_JOB,
            payload :{
                loading : true,
                data : false,
                error : false
            }
        })

        // Datanya
        axios({
            method : 'GET',
            url : "http://dev3.dansmultipro.co.id/api/recruitment/positions.json",
            timeout :120000
        })
        .then((Response) =>{
            console.log('3.berhasil')
            dispatch({
                type : GET_LIST_JOB,
                payload :{
                    loading : false,
                    data : Response.data,
                    error : true
                }
            })
        })
        // gagal
        .catch((error) =>{
            dispatch({
                type : GET_LIST_JOB,
                payload :{
                    loading : false,
                    data : false,
                    error : true
                }
            })
        })
    }
}
