import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { jobList } from '../../redux/action/jobAction';
import Card from '../card/card';

const ListJob = () => {
    const dispatch = useDispatch()
    const { getListJob, getListJobLoading, getListJobError, searchListJob, searchListJobLoading, searchListJobError } = useSelector((state) => state.JobReducer)
    const jumlah=0;

    useEffect(() => {
        // paggil
        console.log('komponen didmount')
        dispatch(jobList())
        const obj = { a: 1, b: 2, c: 3 };

        for (const prop in obj) {
            console.log(`obj.${prop} = ${obj[prop]}`);
            jumlah = jumlah + 1
            console.log(jumlah)

        }

    }, [dispatch])

    useEffect(() => {
        if (searchListJob) {
            console.log('data: true')
        }
    }, [dispatch])

    return (
        <div className='container mt-4'>
            <h3 className='pb-3'>Lowongan Pekerjaan yang tersedia</h3>
            <h3 className='pb-3'>{jumlah}</h3>
            {searchListJob ? searchListJob.map((job) => {
                return (
                    <div>
                        <Card title={job.title} type={job.type} company={job.company} location={job.location} />
                    </div>
                )
            }) : getListJob ? (
                getListJob.map((job) => {

                    return (
                        <div>

                            <Card title={job.title} type={job.type} company={job.company} location={job.location} />
                        </div>
                    )
                })
            ) : 'loading'}
        </div>
    );
}

export default ListJob;
