import axios from "axios";

export const GET_LIST_JOB = 'GET_LIST_JOB'

export const jobList = () => {
    return (dispatch) => {
        // loading 
        dispatch({
            type: GET_LIST_KONTAK,
            payload: {
                loading: true,
                data: false,
                errorMassage: false
            }
        })
        // get Api 
        axios({
            method: 'GET',
            url: "http://dev3.dansmultipro.co.id/api/recruitment/positions.json",
            timeout: 120000
        })
            .then((Response) => {
                // berhasil
                dispatch({
                    type: GET_LIST_KONTAK,
                    payload: {
                        loading: false,
                        data: Response.data,
                        errorMassage: false
                    }
                })
            })
            .catch((error) => {
                console.log("gagal")
                dispatch({
                    type: GET_LIST_KONTAK,
                    payload: {
                        loading: false,
                        data: false,
                        errorMassage: error.massage
                    }
                })
            })
    }
}
